import React, { useEffect, useState } from 'react'
import './App.css'
import { unstable_useViewTransitionState, useLocation } from 'react-router-dom'
import {io} from 'socket.io-client'

const socket = io.connect("http://localhost:3001")


function Content(props) {

    // const location = useLocation()
    // const players = location.state
    // console.log(players);
    const [player, setPlayer] = useState("")


    const handleEvent = () =>{
        // console.log("Handle Event");
        document.getElementById("next-btn").innerHTML = "Next"
        socket.emit("req_player")
    }

    const handleSold = () =>{
        const cur_team = document.getElementById("bid-team")
        const cur_amount = document.getElementById("bid-amount")
        console.log(player);
        socket.emit("sold",{cur_team, cur_amount, player})
    }

    const handleUnSold = () =>{

    }
    useEffect(()=>{
        socket.on("requested_player",(data)=>{
            console.log("data.player "+data);
            setPlayer(data)
        })

        socket.on("sold_success", ()=>{
            alert("Player sold. Select next player.")
        })

        return () => {
            socket.off("requested_player");
            socket.off("sold_success");
        };  
    },[socket])
    

    return (
        <div className='container'>
            <div className="top-left">
                <p>Set No: <span id="set-no">{player["Set_No"]}</span></p>
                <p>Set Name: <span id="set-name">{player["Set_Name"]}</span></p>
            </div>
            <div className="top-right">
                <div className="timer-section">
                    <p>Timer: <span id="timer">00:00</span></p>
                </div>

            </div>
            <div className="bottom-left">
                <p><b>Player Name: </b><span id="player-name">{player["First_Name"]+" "+player["Surname"]}</span></p>
                <h2>Player Stats</h2>
                <p>Stats: <span id="player-stats">Stats Here</span></p>
            </div>
            <div className="center">
                <h2>Auction Details</h2>  
                <p>Base Price: <span id="base-price">{player["Base_Price_Lakhs"]}</span></p>
                <p>Current Bid: <input type="text" value={"-"} id="bid-team" readOnly/> | <input type="text" value={"0"} id="bid-amount" readOnly/></p>
            </div>
            
            <div className="bottom-right">
                <button id="unsold-btn" onClick={handleUnSold} >Unsold</button>
                <button id="sold-btn" onClick={handleSold}>Sold</button>
                <button id="next-btn" onClick={handleEvent}>Start</button>
            </div>
        </div>
    )
}

export default Content
