import React, {useState, useEffect } from 'react'
import './App.css'
import {io} from 'socket.io-client'
import {useLocation} from 'react-router-dom'

const socket = io.connect("http://localhost:3001")

function UserContent() {

    const location = useLocation()
    // console.log(location.state[0]);
    // const player = location.state
    const [player, setPlayer] = useState("")
    const [team, setTeam] = useState("")
    const [c_team, setCTeam] = useState("")
    const [c_amount, setCAmount] = useState(0)
    var bp=""
    // setTimeout(()=>{
        // socket.emit("req_player")
    // }, 2000)

    const handleBid = () =>{

        var val = document.getElementById('bid-amount')
        // console.log(amount);
        // var my_team = document.getElementById('bid-team')
        // setCAmount(c_amount + 10)
        // amount.value= Number(amount.value) + c_amount
        val.value = Number(val.value)+10
        socket.emit("handle_bid",{team, amount:val.value})
        
        
        

    }
    useEffect(()=>{

        setCAmount(0)

        socket.emit("req_player")

        socket.on("requested_player",(data)=>{
            console.log("hey  "+data);
            setPlayer(data)
            bp = Number(player["Base_Price_Lakhs"])
            // console.log(bp);
            setCAmount(bp)
            setTeam(location.state) 
        })

        socket.on("reply_handle_bid", (data)=>{
            setCTeam(data.team)
            setCAmount(data.amount)
        })

    },[socket])

    return (
        <div>
            <p>User Content</p>
            <div className='container'>
                <div className="top-right">
                    <div className="timer-section">
                        <p>Timer: <span id="timer">00:00</span></p>
                    </div>

                </div>
                <div className="bottom-left">
                    <p><b>Player Name: </b><span id="player-name">{player["First_Name"]+" "+player["Surname"]}</span></p>
                    <h2>Player Stats</h2>
                    <p>Stats: <span id="player-stats">Stats Here</span></p>
                </div>
                <div className="center">
                    <h2>Auction Details</h2>
                    <p>Base Price: <span id="base-price">{player["Base_Price_Lakhs"]}</span></p>
                    <p>Current Bid: <input type="text" value={"-"} id="bid-team" readOnly/> | <input type="number" value={"-"} id="bid-amount" readOnly/></p>
                </div>
                <div className="table">
                    <table>
                        <thead>
                            <tr>
                                <th>Slots</th>
                                <th>Players</th>
                                <th>Over.</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>Batsman</td>
                                <td>5</td>
                                <td>2</td>
                            </tr>
                            <tr>
                                <td>Bowler</td>
                                <td>7</td>
                                <td>3</td>
                            </tr>
                            <tr>
                                <td>All Rounder</td>
                                <td>4</td>
                                <td>2</td>
                            </tr>
                            <tr>
                                <td>WK</td>
                                <td>2</td>
                                <td>1</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
                <div className="bottom-right">
                    <button id="bid-btn" onClick={handleBid} >Make Bid</button>
                </div>
            </div>
        </div>
    )
}

export default UserContent
