import React, { useState, useEffect } from 'react'
import './App.css'
import { io } from 'socket.io-client'
import { useLocation } from 'react-router-dom'
import Stats from './Stats'

const socket = io.connect("http://localhost:3001")

window.addEventListener('beforeunload', function (e) {
    // Cancel the event
    e.preventDefault();
    // Chrome requires returnValue to be set
});


function UserContent() {

    const location = useLocation()
    const [player, setPlayer] = useState("")
    const [team, setTeam] = useState("")
    const [c_team, setCTeam] = useState("")
    const [c_amount, setCAmount] = useState(0)
    const [basePrice, setBasePrice] = useState(0)
    const [timer, setTimer] = useState(30);
    const [timerRunning, setTimerRunning] = useState(false);

    let bp


    const handleBid = () => {
        setBasePrice(basePrice)
        console.log(basePrice);
        setCAmount(c_amount)
        setCTeam(team)
        console.log("After " + c_amount);
        socket.emit("handle_bid", { team, amount: c_amount, basePrice })


    }
    useEffect(() => {
        let intervalId;
        // socket.emit("req_player", () => {
        //     setTimer(30);
        //     setTimerRunning(true);
        // })
        socket.emit("req_player")

        socket.on("requested_player", (data) => {
            // console.log("hey  "+data.player);
            setPlayer(data.player)
            setTeam(location.state)
            setCAmount(0)
            setBasePrice(Number(data.player["Base_Price_Lakhs"]))

        })

        socket.on("sold_success", (data) => {
            alert(`Player sold to ${data.cur_team}`)
            setCTeam("-")
        })

        socket.on("reply_handle_bid", (data) => {
            setCTeam(data.team)
            setCAmount(data.amount)
            // console.log(c_amount);
            // setTimer(15); // Reset timer to 15 seconds
            // setTimerRunning(true);
        })


    }, [socket])

    // useEffect(() => {
    //     let intervalId;

    //     if (timerRunning) {
    //         intervalId = setInterval(() => {
    //             setTimer((prevTimer) => {
    //                 if (prevTimer > 0) {
    //                     return prevTimer - 1;
    //                 } else {
    //                     clearInterval(intervalId);
    //                     setTimerRunning(false);
    //                     // Perform actions when timer reaches 0
    //                     // For example, emit an event or show a message
    //                     return 0;
    //                 }
    //             });
    //         }, 1000);
    //     }

    //     return () => clearInterval(intervalId);
    // }, [timerRunning]);

    return (
        <div>
            <div className='container'>
                <h1>{team}</h1>
                <div className="top-right">
                    <div className="timer-section">
                        <p>Timer: <span id="timer">{timer}</span></p>
                    </div>
                </div>

                <div className="center">
                    <div className="bottom-left">
                        <p><b>Player Name: </b><span id="player-name">{player["First_Name"] + " " + player["Surname"]}</span></p>
                        <h2>Player Stats</h2>
                        {/* <p>Stats: <span id="player-stats">Stats Here</span></p> */}
                        <Stats player={player} />
                    </div>
                    <div className='auction-details'>
                        <h2>Auction Details</h2>
                        <p>Base Price: <input type="text" value={player["Base_Price_Lakhs"]} id="bid-team" readOnly /></p>
                        <p>Current Bid: <input type="text" value={c_team} id="bid-team" readOnly /> | <input type="text" value={c_amount} id="bid-amount" readOnly /></p>
                    </div>
                </div>

                <div className="table">
                    <table>
                        <thead>
                            <tr>
                                <th>Slots</th>
                                <th>Players</th>
                                <th>Over.</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>Batsman</td>
                                <td>5</td>
                                <td>2</td>
                            </tr>
                            <tr>
                                <td>Bowler</td>
                                <td>7</td>
                                <td>3</td>
                            </tr>
                            <tr>
                                <td>All Rounder</td>
                                <td>4</td>
                                <td>2</td>
                            </tr>
                            <tr>
                                <td>WK</td>
                                <td>2</td>
                                <td>1</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
                <div className="bottom-right">
                    <button id="bid-btn" onClick={handleBid} >Make Bid</button>
                </div>
            </div>
        </div>
    )
}

export default UserContent
