import {io} from 'socket.io-client';
import './App.css';

import {useEffect, useState} from 'react'

const socket = io.connect("http://localhost:3001")



function App() {
  const [Room, setRoom] = useState("")
  const [Message, setMessage] = useState("")
  const [ReceiveMsg, setReceiveMsg] = useState("")

  const sendRoom = ()=>{
    // socket.emit("set_roomno", {Room})
    if (Room.trim() !== "") {
      socket.emit("set_roomno", Room);
    } else {
      console.log("Please enter a room number.");
    }
  }

  const sendMessage = () =>{ 
      socket.emit("send_message",{message : Message , Room})
  }

  useEffect(()=>{
      socket.on("receive_message",(data)=>{
          // alert(data.message)
          setReceiveMsg(data)

      })
  }, [socket])

  return (
    <div className="App">
      <input type='text' placeholder='Enter Room No.' onChange={(event)=>{
          setRoom(event.target.value)
      }} />
      <button onClick={sendRoom}>Join</button>
      <input type='text' placeholder='Enter your message' onChange={(event)=>{
          setMessage(event.target.value)
      }} />
      <button onClick={sendMessage}>Send</button>
      <h1>Message : </h1>
      <p>{ReceiveMsg}</p>
    </div>
  );
}

export default App;
