import React, { useState, useEffect } from 'react'
import './App.css'
import { io } from 'socket.io-client'
import { useLocation } from 'react-router-dom'
import Stats from './Stats'
import Slots from './Slots'
import {ToastContainer, toast} from 'react-toastify'

const socket = io.connect("http://localhost:3001")


function UserContent() {

    const location = useLocation()
    const [player, setPlayer] = useState("")
    const [team, setTeam] = useState("")
    const [c_team, setCTeam] = useState("")
    const [c_amount, setCAmount] = useState(0)
    const [basePrice, setBasePrice] = useState(0)
    const [timer, setTimer] = useState(15);
    const [timerRunning, setTimerRunning] = useState(false)
    const [isDisabled, setIsDisabled] = useState(false);
    const [teamData, setTeamData] = useState(null)
    const [purse, SetPurse] =useState(0)

    const handleBid = () => {
        setBasePrice(basePrice)
        console.log(c_amount);
        if(c_amount < purse){
            setCAmount(c_amount)
            setCTeam(team)
            socket.emit("handle_bid", { team, amount: c_amount, basePrice , purse})
        }
        else{
            alert("You are exceeding your purse limit")
            toast.error("Watch out your purse before making a bid!!!")
        }
    }

    useEffect(()=>{
        if(team){
            setTeamData(location.state[1])
            SetPurse(location.state[2])
        }
    },[team])

    useEffect(() => {
    
        socket.emit("req_player", () => {
            setTimer(30);
            setTimerRunning(true);
        })

        socket.on("requested_player", (data) => {
            setPlayer(data.player)
            setTeam(location.state[0])
            setCAmount(0)
            setBasePrice(Number(data.player["Base_Price_Lakhs"]))

        })

        socket.on("sold_success", (data) => {
            toast.info(`Player sold to ${data.data.cur_team}`)
            setCTeam("-")
            SetPurse(data.purse)
            setIsDisabled(false)    
            setTimer(15)
            setTimerRunning(0)
            
        })

        socket.on("reply_handle_bid", (data) => {
            setCTeam(data.team)
            setCAmount(data.amount)
            
            setTimer(15);
            setTimerRunning(true);
        })


    }, [socket])

    useEffect(() => {
        let intervalId;

        if (timerRunning) {
            intervalId = setInterval(() => {
                setTimer((prevTimer) => {
                    if (prevTimer > 0) {
                        socket.emit("timer_update", prevTimer-1)
                        return prevTimer - 1;
                    } else {
                        clearInterval(intervalId);
                        setTimerRunning(false);
                        toast.error("Time over. No more bids allowed")
                        setIsDisabled(true)
                        return 0;
                    }
                });
            }, 1000);
        }

        if(team){
            console.log();
        }

        return () => clearInterval(intervalId);
    }, [timerRunning]);

    return (
        <div>
            <div className='container'>
                <ToastContainer />
                <h1>{team}</h1>
                <div className="top-right">
                    <div className="timer-section">
                        <p>Timer: <span id="timer">{timer}</span></p>
                    </div>
                </div>

                <div className="center">
                    <div className="bottom-left">
                        <p><b>Player Name: </b><span id="player-name">{player["First_Name"] + " " + player["Surname"]}</span></p>
                        <h2>Player Stats</h2>
                        {/* <p>Stats: <span id="player-stats">Stats Here</span></p> */}
                        <Stats player={player} />
                    </div>
                    <div className='auction-details'>
                        <h2>Auction Details</h2>
                        <p>Base Price: <input type="text" value={player["Base_Price_Lakhs"]} id="bid-team" readOnly /></p>
                        <p>Current Bid: <input type="text" value={c_team} id="bid-team" readOnly /> | <input type="text" value={c_amount} id="bid-amount" readOnly /></p>
                    </div>
                </div>

                {teamData && <div className="table">
                    <Slots slots = {teamData} />
                </div>}
                <div className="bottom-right">
                    <button id="bid-btn" onClick={handleBid} disabled={isDisabled} >Make Bid</button>
                </div>
            </div>
        </div>
    )
}

export default UserContent
